it(`kitchen sink`, () => {
  const options = {
    accessToken: `abcdefghijklmnopqrstuvwxyz`,
    spaceId: `abcdefgh`,
    host: `wat`,
    localeFilter: locale => locale.code === `de`
  };
  const annotations = {
    spaceId: `foo`,
    environment: `bar`
  };
  const result = formatPluginOptionsForCLI(options, annotations);
  const lines = result.split(`\n`);

  // doesn't leak secrets when listing plugin options
  expect(result).toContain(`accessToken`);
  expect(result).not.toContain(options.accessToken);
  expect(result).toContain(`spaceId`);
  expect(result).not.toContain(options.spaceId);

  // mark if default value is used
  expect(lines.find(line => line.includes(`downloadLocal`))).toContain(`default value`);
  expect(lines.find(line => line.includes(`useNameForId`))).toContain(`default value`);
  expect(lines.find(line => line.includes(`environment`))).toContain(`default value`);

  // annotations are added
  expect(lines.find(line => line.includes(`spaceId`))).toContain(annotations.spaceId);
  expect(lines.find(line => line.includes(`environment`))).toContain(annotations.environment);
});

