test(`when passed a deeply nested, complex object literal, it returns all stringified ObjectIds within a nested, complex object literal`, () => {
  const ids = [new ObjectID().toHexString(), new ObjectID().toHexString(), new ObjectID().toHexString(), new ObjectID().toHexString(), new ObjectID().toHexString(), new ObjectID().toHexString()];
  const complexNested = {
    id: new ObjectID(ids[0]),
    connections: [{
      id: new ObjectID(ids[1])
    }, {
      ids: [new ObjectID(ids[2]), new ObjectID(ids[3])]
    }],
    name: {
      first: `sally`,
      last: {
        maiden: {
          val: `smith`,
          id: new ObjectID(ids[4])
        },
        current: {
          val: `wilson`,
          id: new ObjectID(ids[5])
        },
        former: {
          val: `smith`,
          id: new ObjectID(ids[4])
        }
      }
    }
  };
  const expectedResult = {
    id: ids[0],
    connections: [{
      id: ids[1]
    }, {
      ids: [ids[2], ids[3]]
    }],
    name: {
      first: `sally`,
      last: {
        maiden: {
          val: `smith`,
          id: ids[4]
        },
        current: {
          val: `wilson`,
          id: ids[5]
        },
        former: {
          val: `smith`,
          id: ids[4]
        }
      }
    }
  };
  expect(stringifyObjectIds(complexNested)).toEqual(expectedResult);
  expect(stringifyObjectIds(complexNested)).not.toEqual(complexNested);
});

