it('should be invokable from the CLI', async () => {
  const port = 3030;
  const server = http
    .createServer((req, res) => {
      res.writeHead(200);
      const indexPath = path.join(
        __dirname,
        '../../test/fixtures/index.json'
      );
      fs.readFile(indexPath, (err, data) => {
        if (err) {
          res.writeHead(404);
          res.end(JSON.stringify(err));
          return;
        }
        res.writeHead(200);
        res.end(data);
      });
    })
    .listen(port);
  const downloadPath = 'build/test/temp';
  await execa('node', [
    './build/src/generator/download',
    '--download-path',
    downloadPath,
    '--discovery-url',
    `http://localhost:${port}/`,
  ]);
  assert.ok(fs.existsSync('build/test/temp'));
  server.close();
});

it('should flatten an object schema', () => {
  const input = {
    a: {
      b: {
        c: [1, 2, 3],
      },
      d: 'test',
    },
    e: 5,
  };
  const expected = {
    'a.b.c': [1, 2, 3],
    'a.d': 'test',
    e: 5,
  };
  const result = dn.flattenObject(input);
  assert.deepStrictEqual(result, expected);
});

it('should get the diff between two flattened lists', () => {
  const oldDoc = {
    a: 1,
    b: 2,
    c: [1, 2, 3],
  };
  const newDoc = {
    a: 1,
    c: [1, 2, 4],
    d: 5,
  };
  const expected = [
    {
      action: 'DELETED',
      keyName: 'b',
    },
    {
      action: 'ADDED',
      keyName: 'd',
    },
    {
      action: 'CHANGED',
      keyName: 'c',
    },
  ];
  const result = dn.getDiffs(oldDoc, newDoc);
  assert.deepStrictEqual(result, expected);
});

