  const realPath = path.resolve('jwt.keys.json');
  const exists = fs.existsSync(realPath);

  if (!exists) {
    const contents = fs.readFileSync(fakePath);
    fs.writeFileSync(realPath, contents);
  }

  const data = await samples.jwt.runSample();
  assert(data);
  scope.done();
});

