it('should test keys(array)', function () {
  expect({
    foo: 1
  }).to.have.keys(['foo']);
  expect({
    foo: 1,
    bar: 2
  }).to.have.keys(['foo', 'bar']);
  expect({
    foo: 1,
    bar: 2
  }).to.have.keys('foo', 'bar');
  expect({
    foo: 1,
    bar: 2,
    baz: 3
  }).to.include.keys('foo', 'bar');
  expect({
    foo: 1,
    bar: 2,
    baz: 3
  }).to.include.keys('bar', 'foo');
  expect({
    foo: 1,
    bar: 2,
    baz: 3
  }).to.include.keys('baz');
  expect({
    foo: 1,
    bar: 2
  }).to.include.keys('foo');
  expect({
    foo: 1,
    bar: 2
  }).to.include.keys('bar', 'foo');
  expect({
    foo: 1,
    bar: 2
  }).to.include.keys(['foo']);
  expect({
    foo: 1,
    bar: 2
  }).to.include.keys(['bar']);
  expect({
    foo: 1,
    bar: 2
  }).to.include.keys(['bar', 'foo']);
  expect({
    foo: 1,
    bar: 2
  }).to.not.have.keys('baz');
  expect({
    foo: 1,
    bar: 2
  }).to.not.have.keys('foo', 'baz');
  expect({
    foo: 1,
    bar: 2
  }).to.not.include.keys('baz');
  expect({
    foo: 1,
    bar: 2
  }).to.not.include.keys('foo', 'baz');
  expect({
    foo: 1,
    bar: 2
  }).to.not.include.keys('baz', 'foo');
  err(function () {
    expect({
      foo: 1
    }).to.have.keys();
  }, "keys required");
  err(function () {
    expect({
      foo: 1
    }).to.have.keys([]);
  }, "keys required");
  err(function () {
    expect({
      foo: 1
    }).to.not.have.keys([]);
  }, "keys required");
  err(function () {
    expect({
      foo: 1
    }).to.include.keys([]);
  }, "keys required");
  err(function () {
    expect({
      foo: 1
    }).to.have.keys(['bar']);
  }, "expected { foo: 1 } to include key 'bar'");
  err(function () {
    expect({
      foo: 1
    }).to.have.keys(['bar', 'baz']);
  }, "expected { foo: 1 } to include keys 'bar', and 'baz'");
  err(function () {
    expect({
      foo: 1
    }).to.have.keys(['foo', 'bar', 'baz']);
  }, "expected { foo: 1 } to include keys 'foo', 'bar', and 'baz'");
  err(function () {
    expect({
      foo: 1
    }).to.not.have.keys(['foo']);
  }, "expected { foo: 1 } to not include key 'foo'");
  err(function () {
    expect({
      foo: 1
    }).to.not.have.keys(['foo']);
  }, "expected { foo: 1 } to not include key 'foo'");
  err(function () {
    expect({
      foo: 1,
      bar: 2
    }).to.not.have.keys(['foo', 'bar']);
  }, "expected { foo: 1, bar: 2 } to not include keys 'foo', and 'bar'");
  err(function () {
    expect({
      foo: 1
    }).to.not.include.keys(['foo']);
  }, "expected { foo: 1 } to not include key 'foo'");
  err(function () {
    expect({
      foo: 1
    }).to.include.keys('foo', 'bar');
  }, "expected { foo: 1 } to include keys 'foo', and 'bar'"); // only

  expect({
    foo: 1,
    bar: 1
  }).to.only.have.keys('foo', 'bar');
  expect({
    foo: 1,
    bar: 1
  }).to.only.have.keys(['foo', 'bar']);
  err(function () {
    expect({
      a: 'b',
      c: 'd'
    }).to.only.have.keys('a', 'b', 'c');
  }, "expected { a: 'b', c: 'd' } to only have keys 'a', 'b', and 'c'");
  err(function () {
    expect({
      a: 'b',
      c: 'd'
    }).to.only.have.keys('a');
  }, "expected { a: 'b', c: 'd' } to only have key 'a'");
});

