});

it('increases status.times count and reruns up to 4 times', async () => {
  execStub.returns(Promise.resolve({
    _cronSignature: 'RUNNING_CRON'
  }));
  execStub.onCall(4).returns(Promise.resolve({
    _cronSignature: 'NOT_RUNNING'
  }));
  await recoverCron(status, locals);
  expect(status.times).to.eql(4);
  expect(locals.user).to.eql({
    _cronSignature: 'NOT_RUNNING'
  });
});

