    _id: leader._id
  });
  updatedLeadersTasks = await Tasks.Task.find({
    _id: {
      $in: updatedLeader.tasksOrder[`${taskType}s`]
    }
  });
  const updatedSyncedTask = find(updatedLeadersTasks, updatedLeadersTask => updatedLeadersTask._id === syncedTask._id);
  expect(task.group.assignedUsers).to.not.contain(leader._id);
  expect(updatedSyncedTask).to.exist;
  expect(updatedSyncedTask.group._id).to.be.undefined;
});

