  user = await User.findById(user._id).select('-items').exec();
  expect(user.isSelected('items')).to.equal(false); // create the conditions for the beast master achievement
  // but this time it should not be awarded

  user.achievements.beastMasterCount = 3;
  user = await user.save();
  expect(user.achievements.beastMaster).to.equal(false); // reset

  user.achievements.beastMasterCount = 0;
  user = await user.save(); // this time with achievements not selected

