  await leader.post(`/groups/${questingGroup._id}/quests/invite/${PET_QUEST}`);
  await partyMembers[0].post(`/groups/${questingGroup._id}/quests/accept`); // partyMembers[1] hasn't accepted the invitation, because if he accepts, invitation phase ends.
