});

it('is equal to daystart o\'clock when daystart configured', () => {
  const options = {
    now: moment('2020-02-02 09:30:00Z'),
    timezoneOffset: 0,
    dayStart: 5
  };
  const result = startOfDay(options);
  expect(result).to.be.sameMoment('2020-02-02 05:00:00Z');
});

