  const options = {
    now: todayAtFivePm,
    dayStart: 11
  };
  const result = daysSince(oneWeekAgoAtEightAm, options);
  expect(result).to.equal(8);
}); // Variations in timezone configuration options are already covered by startOfDay tests.

