it('flattenAndNamespace', () => {
  const result = flattenAndNamespace({
    nested: {
      computed({
        state
      }, ...args) {
        return [state.name, ...args];
      },

      getName({
        state
      }, ...args) {
        return [state.name, ...args];
      }

    },
    nested2: {
      getName({
        state
      }, ...args) {
        return [state.name, ...args];
      }

    }
  });
