QUnit.test('DataCursor.emitCursor', function (assert) {
    const done = assert.async(2),
        event = new Event('event'),
        cursor = new DataCursor(),
        table = new DataTable({
            columns: {
                a: [0, 1, 2],
                b: [10, 11, 12]
            }
        });

    cursor
        .addListener(table.id, 'test2', function (e) {
            const expectedCursor = {
                type: 'range',
                firstRow: 2,
                lastRow: 9,
                state: 'test2'
            };

            assert.strictEqual(
                this,
                cursor,
                'Listener scope should be a DataCursor instance by default.'
            );

            assert.deepEqual(
                e,
                {
                    cursor: expectedCursor,
                    cursors: [],
                    table
                },
                'Emitted event should have expected structure.'
            );

            done();
        })
        .addListener(table.id, 'test3', function (e) {
            const expectedCursor = {
                type: 'range',
                firstRow: 0,
                lastRow: 2,
                state: 'test3'
            };

            assert.strictEqual(
                this,
                cursor,
                'Listener scope should be a DataCursor instance by default.'
            );

            assert.deepEqual(
                e,
                {
                    cursor: expectedCursor,
                    cursors: [expectedCursor],
                    event,
                    table
                },
                'Lasting event should have expected structure.'
            );

            done();
        });

    cursor
        .emitCursor(table, {
            type: 'position',
            column: 'a',
            row: 1,
            state: 'test1'
        })
        .emitCursor(table, {
            type: 'range',
            firstRow: 2,
            lastRow: 9,
            state: 'test2'
        })
        .emitCursor(table, {
            type: 'range',
            firstRow: 0,
            lastRow: 2,
            state: 'test3'
        }, event, true);
});

