        DataCursor.isEqual({
            type: 'position',
            state: 'test2a'
        }, {
            type: 'position',
            state: 'test2b'
        }),
        'Cursors should not be equal.'
    );
    assert.ok(
        DataCursor.isEqual({
            type: 'position',
            column: 'a',
            state: 'test3'
        }, {
            type: 'position',
            column: 'a',
            state: 'test3'
        }),
        'Cursors should be equal.'
    );
    assert.notOk(
        DataCursor.isEqual({
            type: 'position',
            column: 'a',
            state: 'test4'
        }, {
            type: 'position',
            column: 'b',
            state: 'test4'
        }),
        'Cursors should not be equal.'
    );
    assert.notOk(
        DataCursor.isEqual({
            type: 'position',
            column: 'a',
