it('Cypress.minimatch - test out glob patterns against strings', () => {
  // https://on.cypress.io/minimatch
  let matching = Cypress.minimatch('/users/1/comments', '/users/*/comments', {
    matchBase: true
  });
  expect(matching, 'matching wildcard').to.be.true;
  matching = Cypress.minimatch('/users/1/comments/2', '/users/*/comments', {
    matchBase: true
  });
  expect(matching, 'comments').to.be.false; // ** matches against all downstream path segments

  matching = Cypress.minimatch('/foo/bar/baz/123/quux?a=b&c=2', '/foo/**', {
    matchBase: true
  });
  expect(matching, 'comments').to.be.true; // whereas * matches only the next path segment

  matching = Cypress.minimatch('/foo/bar/baz/123/quux?a=b&c=2', '/foo/*', {
    matchBase: false
  });
  expect(matching, 'comments').to.be.false;
});

it('Cypress.moment() - format or parse dates using a moment method', () => {
  // https://on.cypress.io/moment
  const time = Cypress.moment('2014-04-25T19:38:53.196Z').utc().format('h:mm A');
  expect(time).to.be.a('string');
  cy.get('.utility-moment').contains('3:38 PM').should('have.class', 'badge'); // the time in the element should be between 3pm and 5pm

  const start = Cypress.moment('3:00 PM', 'LT');
  const end = Cypress.moment('5:00 PM', 'LT');
  cy.get('.utility-moment .badge').should($el => {
    // parse American time like "3:38 PM"
    const m = Cypress.moment($el.text().trim(), 'LT'); // display hours + minutes + AM|PM

    const f = 'h:mm A';
    expect(m.isBetween(start, end), `${m.format(f)} should be between ${start.format(f)} and ${end.format(f)}`).to.be.true;
  });
});

it('Cypress.Promise - instantiate a bluebird promise', () => {
  // https://on.cypress.io/promise
  let waited = false;
  /**
   * @return Bluebird<string>
   */

  function waitOneSecond() {
    // return a promise that resolves after 1 second
    // @ts-ignore TS2351 (new Cypress.Promise)
    return new Cypress.Promise((resolve, reject) => {
      setTimeout(() => {
        // set waited to true
        waited = true; // resolve with 'foo' string

        resolve('foo');
      }, 1000);
    });
  }

  cy.then(() => // return a promise to cy.then() that
  // is awaited until it resolves
  // @ts-ignore TS7006
  waitOneSecond().then(str => {
    expect(str).to.eq('foo');
    expect(waited).to.be.true;
  }));
});

it('cy.viewport() - set the viewport size and dimension', () => {
  // https://on.cypress.io/viewport
  cy.get('#navbar').should('be.visible');
  cy.viewport(320, 480); // the navbar should have collapse since our screen is smaller

  cy.get('#navbar').should('not.be.visible');
  cy.get('.navbar-toggle').should('be.visible').click();
  cy.get('.nav').find('a').should('be.visible'); // lets see what our app looks like on a super large screen

  cy.viewport(2999, 2999); // cy.viewport() accepts a set of preset sizes
  // to easily set the screen to a device's width and height
  // We added a cy.wait() between each viewport change so you can see
  // the change otherwise it is a little too fast to see :)

  cy.viewport('macbook-15');
  cy.wait(200);
  cy.viewport('macbook-13');
  cy.wait(200);
  cy.viewport('macbook-11');
  cy.wait(200);
  cy.viewport('ipad-2');
  cy.wait(200);
  cy.viewport('ipad-mini');
  cy.wait(200);
  cy.viewport('iphone-6+');
  cy.wait(200);
  cy.viewport('iphone-6');
  cy.wait(200);
  cy.viewport('iphone-5');
  cy.wait(200);
  cy.viewport('iphone-4');
  cy.wait(200);
  cy.viewport('iphone-3');
  cy.wait(200); // cy.viewport() accepts an orientation for all presets
  // the default orientation is 'portrait'

  cy.viewport('ipad-2', 'portrait');
  cy.wait(200);
  cy.viewport('iphone-4', 'landscape');
  cy.wait(200); // The viewport will be reset back to the default dimensions
  // in between tests (the  default can be set in cypress.json)
});

