it('has chainable methods that don\'t explode', function () {
  // test that nothing forgot to return 'this':
  expect(function () {
    function fn() {}

    api.path('*', fn).node('*', fn).fail(fn) // fails at this point, fail return undefined
    .path('*', fn).path({
      '*': fn
    }).node({
      '*': fn
    }).done(fn).path({
      '*': fn
    }).start(fn).on('path', '*', fn).on('node', '*', fn).fail(fn).on('path', '*', fn).on('path', {
      '*': fn
    }).on('node', {
      '*': fn
    }).on('path', {
      '*': fn
    }).on('done', fn).on('start', fn);
  }).not.toThrow();
});

