  assert.equal(div.css("display"), "none", "hide() updates inline style of a detached div");
  div.appendTo("#qunit-fixture");
  assert.equal(div.css("display"), "none", "A hidden-while-detached div is hidden after attachment");
  div.show();
  assert.equal(div.css("display"), "block", "A hidden-while-detached div can be shown after attachment");
  div = jQuery("<div class='hidden'>");
  div.show().appendTo("#qunit-fixture");
  assert.equal(div.css("display"), "none", "A shown-while-detached div can be hidden by the CSS cascade");
  div = jQuery("<div><div class='hidden'></div></div>").children("div");
  div.show().appendTo("#qunit-fixture");
  assert.equal(div.css("display"), "none", "A shown-while-detached div inside a visible div can be hidden by the CSS cascade");
  span = jQuery("<span class='hidden'></span>");
  span.show().appendTo("#qunit-fixture");
  assert.equal(span.css("display"), "none", "A shown-while-detached span can be hidden by the CSS cascade");
  div = jQuery("div.hidden");
  div.detach().show();
  assert.ok(!div[0].style.display, "show() does not update inline style of a cascade-hidden-before-detach div");
  div.appendTo("#qunit-fixture");
  assert.equal(div.css("display"), "none", "A shown-while-detached cascade-hidden div is hidden after attachment");
  div.remove();
  span = jQuery("<span class='hidden'></span>");
  span.appendTo("#qunit-fixture").detach().show().appendTo("#qunit-fixture");
  assert.equal(span.css("display"), "none", "A shown-while-detached cascade-hidden span is hidden after attachment");
  span.remove();
  div = jQuery(document.createElement("div"));
  div.show().appendTo("#qunit-fixture");
  assert.ok(!div[0].style.display, "A shown-while-detached div has no inline style");
  assert.equal(div.css("display"), "block", "A shown-while-detached div has default display after attachment");
  div.remove();
  div = jQuery("<div style='display: none'>");
  div.show();
  assert.equal(div[0].style.display, "", "show() updates inline style of a detached inline-hidden div");
  div.appendTo("#qunit-fixture");
  assert.equal(div.css("display"), "block", "A shown-while-detached inline-hidden div has default display after attachment");
  div = jQuery("<div><div style='display: none'></div></div>").children("div");
  div.show().appendTo("#qunit-fixture");
  assert.equal(div.css("display"), "block", "A shown-while-detached inline-hidden div inside a visible div has default display " + "after attachment");
  span = jQuery("<span style='display: none'></span>");
  span.show();
  assert.equal(span[0].style.display, "", "show() updates inline style of a detached inline-hidden span");
  span.appendTo("#qunit-fixture");
  assert.equal(span.css("display"), "inline", "A shown-while-detached inline-hidden span has default display after attachment");
  div = jQuery("<div style='display: inline'></div>");
  div.show().appendTo("#qunit-fixture");
  assert.equal(div.css("display"), "inline", "show() does not update inline style of a detached inline-visible div");
  div.remove();
  tr = jQuery("<tr></tr>");
  jQuery("#table").append(tr);
  tr.detach().hide().show();
  assert.ok(!tr[0].style.display, "Not-hidden detached tr elements have no inline style");
  tr.remove();
  span = jQuery("<span></span>").hide().show();
  assert.ok(!span[0].style.display, "Not-hidden detached span elements have no inline style");
  span.remove();
});

