QUnit.test("Clearing a Cloned Element's Style Shouldn't Clear the Original Element's Style (trac-8908)", function (assert) {
  assert.expect(24);
  var done = assert.async();
  var styles = [{
    name: "backgroundAttachment",
    value: ["fixed"]
  }, {
    name: "backgroundColor",
    value: ["rgb(255, 0, 0)", "rgb(255,0,0)", "#ff0000"]
  }, {
    // Firefox returns auto's value
    name: "backgroundImage",
    value: ["url('test.png')", "url(" + baseURL + "test.png)", "url(\"" + baseURL + "test.png\")"]
  }, {
    name: "backgroundPosition",
    value: ["5% 5%"]
  }, {
    // Firefox returns no-repeat
    name: "backgroundRepeat",
    value: ["repeat-y"]
  }, {
    name: "backgroundClip",
    value: ["padding-box"]
  }, {
    name: "backgroundOrigin",
    value: ["content-box"]
  }, {
    name: "backgroundSize",
    value: ["80px 60px"]
  }];
  jQuery.each(styles, function (index, style) {
    var $clone,
      $clonedChildren,
      $source = jQuery("#firstp"),
      source = $source[0],
      $children = $source.children();
    if (source.style[style.name] === undefined) {
      assert.ok(true, style.name + ": style isn't supported and therefore not an issue");
      assert.ok(true);
      return true;
    }
    $source.css(style.name, style.value[0]);
    $children.css(style.name, style.value[0]);
    $clone = $source.clone();
    $clonedChildren = $clone.children();
    $clone.css(style.name, "");
    $clonedChildren.css(style.name, "");
    window.setTimeout(function () {
      assert.notEqual($clone.css(style.name), style.value[0], "Cloned css was changed");
      assert.ok(jQuery.inArray($source.css(style.name) !== -1, style.value), "Clearing clone.css() doesn't affect source.css(): " + style.name + "; result: " + $source.css(style.name) + "; expected: " + style.value.join(","));
      assert.ok(jQuery.inArray($children.css(style.name) !== -1, style.value), "Clearing clonedChildren.css() doesn't affect children.css(): " + style.name + "; result: " + $children.css(style.name) + "; expected: " + style.value.join(","));
    }, 100);
  });
  window.setTimeout(done, 1000);
});

