QUnit.test("interaction with scrollbars (gh-3589)", function (assert) {
  assert.expect(48);
  var i,
    suffix = "",
    updater = function (adjustment) {
      return function (i, old) {
        return old + adjustment;
      };
    },
    parent = jQuery("<div></div>").css({
      position: "absolute",
      width: "1000px",
      height: "1000px"
    }).appendTo("#qunit-fixture"),
    // Workarounds for IE kill fractional output here.
    fraction = document.documentMode ? 0 : 0.5,
    borderWidth = 1,
    padding = 2,
    size = 100 + fraction,
    plainBox = jQuery("<div></div>").css({
      "box-sizing": "content-box",
      position: "absolute",
      overflow: "scroll",
      width: size + "px",
      height: size + "px"
    }),
    contentBox = plainBox.clone().css({
      border: borderWidth + "px solid blue",
      padding: padding + "px"
    }),
    borderBox = contentBox.clone().css({
      "box-sizing": "border-box"
    }),
    relativeBorderBox = borderBox.clone().css({
      position: "relative"
    }),
    $boxes = jQuery([plainBox[0], contentBox[0], borderBox[0], relativeBorderBox[0]]).appendTo(parent);
  for (i = 0; i < 3; i++) {
    if (i === 1) {
      suffix = " after increasing inner* by " + i;
      size += i;
      $boxes.innerWidth(updater(i)).innerHeight(updater(i));
    } else if (i === 2) {
      suffix = " after increasing outer* by " + i;
      size += i;
      $boxes.outerWidth(updater(i)).outerHeight(updater(i));
    }
    assert.equal(plainBox.innerWidth(), size, "plain content-box innerWidth includes scroll gutter" + suffix);
    assert.equal(plainBox.innerHeight(), size, "plain content-box innerHeight includes scroll gutter" + suffix);
    assert.equal(plainBox.outerWidth(), size, "plain content-box outerWidth includes scroll gutter" + suffix);
    assert.equal(plainBox.outerHeight(), size, "plain content-box outerHeight includes scroll gutter" + suffix);
    assert.equal(contentBox.innerWidth(), size + 2 * padding, "content-box innerWidth includes scroll gutter" + suffix);
    assert.equal(contentBox.innerHeight(), size + 2 * padding, "content-box innerHeight includes scroll gutter" + suffix);
    assert.equal(contentBox.outerWidth(), size + 2 * padding + 2 * borderWidth, "content-box outerWidth includes scroll gutter" + suffix);
    assert.equal(contentBox.outerHeight(), size + 2 * padding + 2 * borderWidth, "content-box outerHeight includes scroll gutter" + suffix);
    assert.equal(borderBox.innerWidth(), size - 2 * borderWidth, "border-box innerWidth includes scroll gutter" + suffix);
    assert.equal(borderBox.innerHeight(), size - 2 * borderWidth, "border-box innerHeight includes scroll gutter" + suffix);
    assert.equal(borderBox.outerWidth(), size, "border-box outerWidth includes scroll gutter" + suffix);
    assert.equal(borderBox.outerHeight(), size, "border-box outerHeight includes scroll gutter" + suffix);
    assert.equal(relativeBorderBox.innerWidth(), size - 2 * borderWidth, "relative border-box innerWidth includes scroll gutter" + suffix);
    assert.equal(relativeBorderBox.innerHeight(), size - 2 * borderWidth, "relative border-box innerHeight includes scroll gutter" + suffix);
    assert.equal(relativeBorderBox.outerWidth(), size, "relative border-box outerWidth includes scroll gutter" + suffix);
    assert.equal(relativeBorderBox.outerHeight(), size, "relative border-box outerHeight includes scroll gutter" + suffix);
  }
});

