    origWidth = parseFloat(foo.css("width")),
    order = [];
  foo.animate({
    width: origWidth + 100
  }, {
    queue: "name",
    duration: 1,
    complete: function () {
      // second callback function
      order.push(2);
      assert.equal(parseFloat(foo.css("width")), origWidth + 100, "Animation ended");
      assert.equal(foo.queue("name").length, 1, "Queue length of 'name' queue");
