QUnit.test("line-height animates correctly (trac-13855)", function (assert) {
  assert.expect(12);
  var t0,
    clock = this.clock,
    longDuration = fxInterval * 200,
    shortDuration = fxInterval * 50,
    animated = jQuery("<p style='line-height: 100;'>unitless</p>" + "<p style='line-height: 5000px;'>px</p>" + "<p style='line-height: 5000%;'>percent</p>" + "<p style='line-height: 100em;'>em</p>").appendTo("#qunit-fixture"),
    initialHeight = jQuery.map(animated, function (el) {
      return jQuery(el).height();
    }),
    tolerance = 1.5;

  // Delay start to improve test stability
  setTimeout(function () {
    t0 = +new Date();
    animated.animate({
      "line-height": "hide"
    }, longDuration, "linear");
    setTimeout(function () {
      var progress = (new Date() - t0) / longDuration;
      animated.each(function (i) {
        var label = jQuery.text(this),
          initial = initialHeight[i],
          height = jQuery(this).height(),
          lower = initial * (1 - progress) / tolerance;
        assert.ok(height < initial, "hide " + label + ": upper bound; " + height + " < " + initial + " @ " + progress * 100 + "%");
        assert.ok(height > lower, "hide " + label + ": lower bound; " + height + " > " + lower + " @ " + progress * 100 + "%");
      });
      t0 = +new Date();
      animated.stop(true, true).hide().animate({
        "line-height": "show"
      }, longDuration, "linear");
      setTimeout(function () {
        var progress = (new Date() - t0) / longDuration;
        animated.each(function (i) {
          var label = jQuery.text(this),
            initial = initialHeight[i],
            height = jQuery(this).height(),
            upper = initial * progress * tolerance;
          assert.ok(height < upper, "show " + label + ": upper bound; " + height + " < " + upper + " @ " + progress * 100 + "%");
        });
        animated.stop(true, true);
      }, shortDuration);
      clock.tick(shortDuration);
    }, shortDuration);
    clock.tick(shortDuration);
  }, fxInterval * 5);
  clock.tick(fxInterval * 5);
});

