    fixture = jQuery("#qunit-fixture").empty(),
    objGlobal = function () {
      return this;
    }(),
    isOk = objGlobal.ok,
    notOk = function () {
      var args = arguments;
      args[0] = !args[0];
      return isOk.apply(this, args);
    };
  objGlobal.ok = notOk;
  scriptsIn = jQuery(["<script type='something/else'>QUnit.assert.ok( false, 'evaluated: non-script' );</script>", "<script type='text/javascript'>QUnit.assert.ok( true, 'evaluated: text/javascript' );</script>", "<script type='text/ecmascript'>QUnit.assert.ok( true, 'evaluated: text/ecmascript' );</script>", "<script>QUnit.assert.ok( true, 'evaluated: no type' );</script>", "<div>", "<script type='something/else'>QUnit.assert.ok( false, 'evaluated: inner non-script' );</script>", "<script type='text/javascript'>QUnit.assert.ok( true, 'evaluated: inner text/javascript' );</script>", "<script type='text/ecmascript'>QUnit.assert.ok( true, 'evaluated: inner text/ecmascript' );</script>", "<script>QUnit.assert.ok( true, 'evaluated: inner no type' );</script>", "</div>"].join(""));
  scriptsIn.appendTo(jQuery("<div class='detached'></div>"));
