QUnit.test("jQuery ready", function (assert) {
  assert.expect(10);
  assert.ok(noEarlyExecution, "Handlers bound to DOM ready should not execute before DOM ready");

  // Ensure execution order.
  assert.deepEqual(order, ["a", "b", "c", "d", "e", "f"], "Bound DOM ready handlers should execute in bind order");

  // Ensure handler argument is correct.
  assert.equal(args.a, jQuery, "Argument passed to fn in jQuery( fn ) should be jQuery");
  assert.equal(args.b, jQuery, "Argument passed to fn in jQuery(document).ready( fn ) should be jQuery");
  order = [];

  // Now that the ready event has fired, again bind to the ready event.
  // These ready handlers should execute asynchronously.
  var done = assert.async();
  jQuery(makeHandler("g"));
  jQuery(document).ready(makeHandler("h"));
  jQuery.ready.then(makeHandler("i"));
  window.setTimeout(function () {
    assert.equal(order.shift(), "g", "Event handler should execute immediately, but async");
    assert.equal(args.g, jQuery, "Argument passed to fn in jQuery( fn ) should be jQuery");
    assert.equal(order.shift(), "h", "Event handler should execute immediately, but async");
    assert.equal(args.h, jQuery, "Argument passed to fn in jQuery(document).ready( fn ) should be jQuery");
    assert.equal(order.shift(), "i", "Event handler should execute immediately, but async");
    assert.equal(args.h, jQuery, "Argument passed to fn in jQuery.ready.then( fn ) should be jQuery");
    done();
  });
});

