  assert.deepEqual(jQuery(attrbad).filter("input[name=foo\\[baz\\]]").get(), q("attrbad_brackets"), "identifier containing brackets");
  assert.deepEqual(jQuery(attrbad).filter("input[data-attr='foo_baz\\']']").get(), q("attrbad_injection"), "string containing quote and right bracket");
  assert.deepEqual(jQuery(attrbad).filter("input[value=\\30 \\30\\37 ]").get(), q("attrbad_leading_digits"), "identifier containing escaped leading digits with whitespace termination");
  assert.deepEqual(jQuery(attrbad).filter("input[value=\\00003007]").get(), q("attrbad_leading_digits"), "identifier containing escaped leading digits without whitespace termination");
  assert.deepEqual(jQuery(attrbad).filter("input[data-attr='\\'']").get(), q("attrbad_quote"), "string containing quote");
  assert.deepEqual(jQuery(attrbad).filter("input[data-attr='\\\\']").get(), q("attrbad_backslash"), "string containing backslash");
  assert.deepEqual(jQuery(attrbad).filter("input[data-attr='\\\\\\'']").get(), q("attrbad_backslash_quote"), "string containing backslash and quote");
  assert.deepEqual(jQuery(attrbad).filter("input[data-attr='\\\\\\\\']").get(), q("attrbad_backslash_backslash"), "string containing adjacent backslashes");
  assert.deepEqual(jQuery(attrbad).filter("input[data-attr='\\5C\\\\']").get(), q("attrbad_backslash_backslash"), "string containing numeric-escape backslash and backslash");
  assert.deepEqual(jQuery(attrbad).filter("input[data-attr='\\5C \\\\']").get(), q("attrbad_backslash_backslash"), "string containing numeric-escape-with-trailing-space backslash and backslash");
  assert.deepEqual(jQuery(attrbad).filter("input[data-attr='\\5C\t\\\\']").get(), q("attrbad_backslash_backslash"), "string containing numeric-escape-with-trailing-tab backslash and backslash");
  assert.deepEqual(jQuery(attrbad).filter("input[data-attr='\\04e00']").get(), q("attrbad_unicode"), "Long numeric escape (BMP)");
  document.getElementById("attrbad_unicode").setAttribute("data-attr", "\uD834\uDF06A");
  assert.deepEqual(jQuery(attrbad).filter("input[data-attr='\\01D306A']").get(), q("attrbad_unicode"), "Long numeric escape (non-BMP)");
});

