it("reflects back cookies set from the server while requesting a script", () => {
  const {
    window
  } = new JSDOM(``, {
    url: testHost + "/TestPath/test-page",
    resources: "usable",
    runScripts: "dangerously"
  });
  const script = window.document.createElement("script");
  const loadPromise = new Promise(resolve => {
    script.onload = () => {
      assertCookies(window.document.cookie, ["Test1=Basic", "Test2=PathMatch", "Test9=Duplicate", "Test10={\"prop1\":5,\"prop2\":\"value\"}", "Malformed"]);
      resolve();
    };
  });
  script.src = testHost + "/TestPath/set-cookie-from-server";
  window.document.body.appendChild(script);
  return loadPromise;
});

