    assertCookies(cookieHeader, ["Test1=Basic", "Test2=PathMatch", "Test6=HttpOnly", "Test9=Duplicate", "Test10={\"prop1\":5,\"prop2\":\"value\"}", "Malformed"]);
    assertCookies(window.document.cookie, ["Test1=Basic", "Test2=PathMatch", "Test9=Duplicate", "Test10={\"prop1\":5,\"prop2\":\"value\"}", "Malformed"]);
  });
});

