it("should disallow custom resource loaders if they doesn't implement ResourceLoader", () => {
  assert.throws(() => new JSDOM(``, {
    resources: {
      fetch() {}

    }
  }), TypeError);

  class MyResourceLoader {
    fetch() {}

  }

  assert.throws(() => new JSDOM(``, {
    resources: new MyResourceLoader()
  }), TypeError);

  function MyResourceLoaderFunction() {
    this.fetch = function () {};
  }

  assert.throws(() => new JSDOM(``, {
    resources: new MyResourceLoaderFunction()
  }), TypeError);
});

it("should send output only to the appropriate window", () => {
  const vc1 = new VirtualConsole();
  const vc2 = new VirtualConsole();
  const dom1 = new JSDOM(``, {
    virtualConsole: vc1
  });
  const dom2 = new JSDOM(``, {
    virtualConsole: vc2
  });
  let vc1Called = false;
  let vc2Called = false;
  vc1.on("log", () => {
    vc1Called = true;
  });
  vc2.on("log", () => {
    vc2Called = true;
  });
  dom1.window.console.log("yay");
  dom2.window.console.warn("yay");
  assert.isTrue(vc1Called, "vc1 must emit a log event");
  assert.isFalse(vc2Called, "vc2 must not have emitted a log event");
});

