  }, elems);
});

it('preserves original order when overriding', () => {
  const elems = new OrderedElements();
  elems.set('a', 1);
  elems.set('b', 1);
  elems.set('a', 2);
  assert.deepEqual({
    elements: {
      a: 2,
      b: 1
    },
    keyOrder: ['a', 'b']
  }, elems);
});

it('can reorder when overriding', () => {
  const elems = new OrderedElements();
  elems.set('a', 1);
  elems.set('b', 1);
  elems.set('a', 2, true);
  assert.deepEqual({
    elements: {
      b: 1,
      a: 2
    },
    keyOrder: ['b', 'a']
  }, elems);
});

it('iterates over the elements in the correct order', () => {
  const elems = new OrderedElements();
  elems.set('a', 1);
  elems.set('b', 2);
  elems.set('c', 3);
  const order = [];
  elems.forEach((value, key) => {
    order.push([key, value]);
  });
  assert.deepEqual([['a', 1], ['b', 2], ['c', 3]], order);
});

it('works with nested Maps', () => {
  const elems = new OrderedElements();
  elems.set('a', 1);
  elems.set('b', new Map([['ba', 1], ['bb', 2]]));
  elems.set('c', 3);
  elems.set('b', new Map([['ba', 3]]));
  assert.deepEqual({
    elements: {
      a: 1,
      b: {
        elements: {
          ba: 3,
          bb: 2
        },
        keyOrder: ['ba', 'bb']
      },
      c: 3
    },
    keyOrder: ['a', 'b', 'c']
  }, elems);
});

