test("git diff call", () => {
  setup(["packages/pkg-1/__tests__/index.test.js", "packages/pkg-1/index.js", "packages/pkg-1/package.json", "packages/pkg-1/README.md"]);
  const hasDiff = makeDiffPredicate("v1.0.0", {
    cwd: "/test"
  });
  const result = hasDiff({
    location: "/test/packages/pkg-1"
  });
  expect(result).toBe(true);
  expect(childProcess.execSync).toHaveBeenLastCalledWith("git", ["diff", "--name-only", "v1.0.0", "--", "packages/pkg-1"], {
    cwd: "/test"
  });
});

test("empty diff", () => {
  setup("");
  const hasDiff = makeDiffPredicate("v1.0.0", {
    cwd: "/test"
  });
  const result = hasDiff({
    location: "/test/packages/pkg-1"
  });
  expect(result).toBe(false);
});

test("rooted package", () => {
  setup("package.json");
