it('can convert AVIF to JPEG', async () => {
  const data = await sharp(inputAvif).resize(32).jpeg().toBuffer();
  const metadata = await sharp(data).metadata();
  const {
    compression,
    size,
    ...metadataWithoutSize
  } = metadata;
  assert.deepStrictEqual(metadataWithoutSize, {
    channels: 3,
    chromaSubsampling: '4:2:0',
    density: 72,
    depth: 'uchar',
    format: 'jpeg',
    hasAlpha: false,
    hasProfile: false,
    height: 13,
    isProgressive: false,
    space: 'srgb',
    width: 32
  });
});

