it('blurred image is smaller than non-blurred', function (done) {
  sharp(fixtures.inputJpg).resize(320, 240).blur(false).toBuffer(function (err, notBlurred, info) {
    if (err) throw err;
    assert.strictEqual(true, notBlurred.length > 0);
    assert.strictEqual('jpeg', info.format);
    assert.strictEqual(320, info.width);
    assert.strictEqual(240, info.height);
    sharp(fixtures.inputJpg).resize(320, 240).blur(true).toBuffer(function (err, blurred, info) {
      if (err) throw err;
      assert.strictEqual(true, blurred.length > 0);
      assert.strictEqual(true, blurred.length < notBlurred.length);
      assert.strictEqual('jpeg', info.format);
      assert.strictEqual(320, info.width);
      assert.strictEqual(240, info.height);
      done();
    });
  });
});

