  .resize(320, 240).joinChannel([fs.readFileSync(fixtures.inputPngTestJoinChannel), // new cyan channel
  fs.readFileSync(fixtures.inputPngStripesH), // new yellow channel
  fs.readFileSync(fixtures.inputPngStripesV) // new black channel
  ]).toColorspace('cmyk').toFormat('jpeg').toBuffer(function (err, data, info) {
    if (err) throw err;
    assert.strictEqual(320, info.width);
    assert.strictEqual(240, info.height);
    assert.strictEqual(4, info.channels);
    fixtures.assertSimilar(fixtures.expected('joinChannel-cmyk.jpg'), data, done);
  });
});

it('Join raw buffers to RGB', function (done) {
  Promise.all([sharp(fixtures.inputPngTestJoinChannel).toColourspace('b-w').raw().toBuffer(), sharp(fixtures.inputPngStripesH).toColourspace('b-w').raw().toBuffer()]).then(function (buffers) {
    sharp(fixtures.inputPng).resize(320, 240).joinChannel(buffers, {
      raw: {
        width: 320,
        height: 240,
        channels: 1
      }
    }).toBuffer(function (err, data, info) {
      if (err) throw err;
      assert.strictEqual(320, info.width);
      assert.strictEqual(240, info.height);
      assert.strictEqual(3, info.channels);
      fixtures.assertSimilar(fixtures.expected('joinChannel-rgb.jpg'), data, done);
    });
  }).catch(function (err) {
    throw err;
  });
});

