it('overlay strong single-channel (sRGB) gaussian noise with 25% transparency over transparent png image', function (done) {
  const output = fixtures.path('output.noise-image-transparent.png');
  const width = 320;
  const height = 240;
  const rawData = {
    width,
    height,
    channels: 1
  };
  const noise = sharp({
    create: {
      width,
      height,
      channels: 1,
      noise: {
        type: 'gaussian',
        mean: 200,
        sigma: 30
      }
    }
  });
  noise.toBuffer(function (err, data, info) {
    if (err) throw err;
    assert.strictEqual(1, info.channels);
    sharp(data, {
      raw: rawData
    }).joinChannel(data, {
      raw: rawData
    }) // r channel
    .joinChannel(data, {
      raw: rawData
    }) // b channel
    .joinChannel(Buffer.alloc(width * height, 64), {
      raw: rawData
    }) // alpha channel
    .toBuffer(function (err, data, info) {
      if (err) throw err;
      assert.strictEqual(4, info.channels);
      sharp(fixtures.inputPngRGBWithAlpha).resize(width, height).composite([{
        input: data,
        blend: 'exclusion',
        raw: {
          width: info.width,
          height: info.height,
          channels: info.channels
        }
      }]).toFile(output, function (err, info) {
        if (err) throw err;
        assert.strictEqual('png', info.format);
        assert.strictEqual(width, info.width);
        assert.strictEqual(height, info.height);
        assert.strictEqual(4, info.channels);
        fixtures.assertSimilar(output, fixtures.inputPngRGBWithAlpha, {
          threshold: 10
        }, function (err) {
          if (err) throw err;
          done();
        });
      });
    });
  });
});

