it("ensures script ordering when not loaded", async () => {
  const cluster = new Cluster([{
    host: "127.0.0.1",
    port: masters[0]
  }]);
  cluster.defineCommand("myget", {
    numberOfKeys: 1,
    lua: "return redis.call('GET', KEYS[1])"
  });
  expect(await cluster.pipeline()
  // @ts-expect-error
  .myget("foo").set("foo", "setAfterMyGET").myget("foo").exec()).to.eql([[null, null], [null, "OK"], [null, "setAfterMyGET"]]);
});

it("falls back to eval when the cache is flushed", async () => {
  const cluster = new Cluster([{
    host: "127.0.0.1",
    port: masters[0]
  }]);
  cluster.defineCommand("myget", {
    numberOfKeys: 1,
    lua: "return redis.call('GET', KEYS[1])"
  });

  // @ts-expect-error
  await cluster.myget("foo");
  for (const node of cluster.nodes("master")) {
    await node.script("FLUSH");
  }
  expect(await cluster.pipeline()
  // @ts-expect-error
  .myget("foo").set("foo", "setAfterMyGET").myget("foo").exec()).to.eql([[null, "setAfterMyGET"], [null, "OK"], [null, "setAfterMyGET"]]);
});

