  await expect(redis.set("foo")).to.eventually.be.rejectedWith("ERR wrong number of arguments for 'set' command");
});

it("should support callbacks in the happy case", done => {
  const redis = new Redis({
    enableAutoPipelining: true
  });
  let value1;
  expect(redis.autoPipelineQueueSize).to.eql(0);
  redis.set("foo1", "bar1", () => {});
  expect(redis.autoPipelineQueueSize).to.eql(1);
  redis.set("foo2", "bar2", () => {
    redis.get("foo1", (err, v1) => {
      expect(err).to.eql(null);
      value1 = v1;
    });
    expect(redis.autoPipelineQueueSize).to.eql(1);
    redis.get("foo2", (err, value2) => {
      expect(err).to.eql(null);
      expect(value1).to.eql("bar1");
      expect(value2).to.eql("bar2");
      expect(redis.autoPipelineQueueSize).to.eql(0);
      done();
    });
    expect(redis.autoPipelineQueueSize).to.eql(2);
  });
  expect(redis.autoPipelineQueueSize).to.eql(2);
});

it("should support callbacks in the failure case", done => {
  const redis = new Redis({
    enableAutoPipelining: true
  });
  expect(redis.autoPipelineQueueSize).to.eql(0);
  redis.set("foo1", "bar1", err => {
    expect(err).to.eql(null);
  });
  expect(redis.autoPipelineQueueSize).to.eql(1);

  // @ts-expect-error
  redis.set("foo2", err => {
    expect(err.message).to.include("ERR wrong number of arguments for 'set' command");
    done();
  });
  expect(redis.autoPipelineQueueSize).to.eql(2);
});

it("should handle callbacks failures", done => {
  const listeners = process.listeners("uncaughtException");
  process.removeAllListeners("uncaughtException");
  process.once("uncaughtException", err => {
    expect(err.message).to.eql("ERROR");
    for (const listener of listeners) {
      process.on("uncaughtException", listener);
    }
    done();
  });
  const redis = new Redis({
    enableAutoPipelining: true
  });
  expect(redis.autoPipelineQueueSize).to.eql(0);
  redis.set("foo1", "bar1", err => {
    expect(err).to.eql(null);
    throw new Error("ERROR");
  });
  redis.set("foo2", "bar2", err => {
    expect(err).to.eql(null);
    expect(redis.autoPipelineQueueSize).to.eql(0);
  });
  expect(redis.autoPipelineQueueSize).to.eql(2);
});

it("should support ASK", done => {
  let asked = false;
  let times = 0;
  const slotTable = [[0, 1, ["127.0.0.1", 30001]], [2, 16383, ["127.0.0.1", 30002]]];
  new MockServer(30001, argv => {
    if (argv[0] === "cluster" && argv[1] === "SLOTS") {
      return slotTable;
    }
    if (argv[0] === "get" && argv[1] === "foo") {
      expect(asked).to.eql(true);
    } else if (argv[0] === "asking") {
      asked = true;
    }
  });
  new MockServer(30002, argv => {
    if (argv[0] === "cluster" && argv[1] === "SLOTS") {
      return slotTable;
    }
    if (argv[0] === "get" && argv[1] === "foo") {
      if (++times === 2) {
        process.nextTick(() => {
          cluster.disconnect();
          done();
        });
      } else {
        return new Error("ASK " + calculateSlot("foo") + " 127.0.0.1:30001");
      }
    }
  });
  var cluster = new Cluster([{
    host: "127.0.0.1",
    port: "30001"
  }], {
    lazyConnect: false
  });
  cluster.get("foo", () => {
    cluster.get("foo");
  });
});

it("should be able to redirect a command to a unknown node", done => {
  let asked = false;
  const slotTable = [[0, 16383, ["127.0.0.1", 30002]]];
  new MockServer(30001, argv => {
    if (argv[0] === "get" && argv[1] === "foo") {
      expect(asked).to.eql(true);
      return "bar";
    } else if (argv[0] === "asking") {
      asked = true;
    }
  });
  new MockServer(30002, argv => {
    if (argv[0] === "cluster" && argv[1] === "SLOTS") {
      return slotTable;
    }
    if (argv[0] === "get" && argv[1] === "foo") {
      return new Error("ASK " + calculateSlot("foo") + " 127.0.0.1:30001");
    }
  });
  const cluster = new Cluster([{
    host: "127.0.0.1",
    port: "30002"
  }]);
  cluster.get("foo", function (err, res) {
    expect(res).to.eql("bar");
    cluster.disconnect();
    done();
  });
});

