it("should retry the command", done => {
  let cluster;
  let times = 0;
  const slotTable = [[0, 16383, ["127.0.0.1", 30001]]];
  new MockServer(30001, argv => {
    if (argv[0] === "cluster" && argv[1] === "SLOTS") {
      return slotTable;
    }
    if (argv[0] === "get" && argv[1] === "foo") {
      if (times++ === 1) {
        process.nextTick(() => {
          cluster.disconnect();
          done();
        });
