it("should work in cluster mode", done => {
  const slotTable = [[0, 5460, ["127.0.0.1", 30001]], [5461, 10922, ["127.0.0.1", 30002]], [10923, 16383, ["127.0.0.1", 30003]]];
  const serverKeys = ["foo1", "foo2", "foo3", "foo4", "foo10"];
