        return ["0", []];
      }
      return [String(cursor + 1), [serverKeys[cursor]]];
    }
  };
  new MockServer(30001, argvHandler);
  new MockServer(30002, argvHandler);
  new MockServer(30003, argvHandler);
  const cluster = new Cluster([{
    host: "127.0.0.1",
