      host: "127.0.0.1",
      port: 27380
    }],
    sentinelRetryStrategy: null,
    name: "master"
  });
  redis.get("foo", error => {
    finish();
    expect(error.message).to.match(/are unreachable/);
  });
  redis.on("error", error => {
    expect(error.message).to.match(/are unreachable/);
    finish();
  });
  redis.on("end", () => {
    finish();
  });
  let pending = 3;
  function finish() {
    if (! --pending) {
      redis.disconnect();
      done();
    }
  }
});

