it("should connect to new master after +switch-master", async () => {
  const sentinel = new MockServer(27379, argv => {
    if (argv[0] === "sentinel" && argv[1] === "get-master-addr-by-name") {
      return ["127.0.0.1", "17380"];
    }
  });
  const master = new MockServer(17380);
  const newMaster = new MockServer(17381);
  const redis = new Redis({
    sentinels: [{
      host: "127.0.0.1",
      port: 27379
    }],
    failoverDetector: true,
    name: "master"
  });
  await Promise.all([once(master, "connect"), once(redis, "failoverSubscribed")]);
  sentinel.handler = function (argv) {
    if (argv[0] === "sentinel" && argv[1] === "get-master-addr-by-name") {
      return ["127.0.0.1", "17381"];
    }
  };
  sentinel.broadcast(["message", "+switch-master", "master 127.0.0.1 17380 127.0.0.1 17381"]);
  await Promise.all([once(redis, "close"),
  // Wait until disconnects from old master
  once(master, "disconnect"), once(newMaster, "connect")]);
  redis.disconnect(); // Disconnect from new master

  await Promise.all([sentinel.disconnectPromise(), master.disconnectPromise(), newMaster.disconnectPromise()]);
});

