  await once(redis, "sentinelReconnecting"); // Wait for the timeout to be set
  clock.tick(1000);
  clock.restore();
  await once(sentinel, "test:resubscribed");
  sentinel.broadcast(["message", "+switch-master", "master 127.0.0.1 17380 127.0.0.1 17381"]);
  await Promise.all([once(redis, "close"),
  // Wait until disconnects from old master
  once(master, "disconnect"), once(newMaster, "connect")]);
