it("should restore subscription after reconnecting(ssubscribe)", done => {
  const redis = new Redis({
    port: 6379,
    host: "127.0.0.1"
  });
  const pub = new Redis({
    port: 6379,
    host: "127.0.0.1"
  });
  // redis.ping(function (err, result) {
  //   // redis.on("message", function (channel, message) {
  //   console.log(`${err}-${result}`);
  //   // });
  // });
  redis.ssubscribe("foo", "bar", function () {
    redis.on("ready", function () {
      // Execute a random command to make sure that `subscribe`
      // is sent
      redis.ping(function () {
        let pending = 2;
        redis.on("smessage", function (channel, message) {
          if (! --pending) {
            redis.disconnect();
            pub.disconnect();
            done();
          }
        });
        pub.spublish("foo", "hi1");
        pub.spublish("bar", "hi2");
      });
    });
    redis.disconnect(true);
  });
});

it("returns numbers as strings", async () => {
  const redis = new Redis({
    stringNumbers: true
  });
  await redis.set("foo", MAX_NUMBER);
  expect(await redis.incr("foo")).to.equal("9007199254740992");
  expect(await redis.incr("foo")).to.equal("9007199254740993");
  expect(await redis.incr("foo")).to.equal("9007199254740994");

