it("should trigger transformer", done => {
  const redis = new Redis();
  let pending = 2;
  const data = {
    name: "Bob",
    age: "17"
  };
  redis.multi().hmset("foo", data).hgetall("foo", function (err, res) {
    expect(res).to.eql("QUEUED");
    if (! --pending) {
      done();
    }
  }).hgetallBuffer("foo").get("foo").getBuffer("foo").exec(function (err, res) {
    expect(res[0][1]).to.eql("OK");
    expect(res[1][1]).to.eql(data);
    expect(res[2][1]).to.eql({
      name: Buffer.from("Bob"),
      age: Buffer.from("17")
    });
    expect(res[3][0]).to.have.property("message", "WRONGTYPE Operation against a key holding the wrong kind of value");
    expect(res[4][0]).to.have.property("message", "WRONGTYPE Operation against a key holding the wrong kind of value");
    if (! --pending) {
      done();
    }
  });
});

it("should trigger transformer inside pipeline", done => {
  const redis = new Redis();
  const data = {
    name: "Bob",
    age: "17"
  };
  redis.pipeline().hmset("foo", data).multi().typeBuffer("foo").hgetall("foo").exec().hgetall("foo").exec(function (err, res) {
    expect(res[0][1]).to.eql("OK");
    expect(res[1][1]).to.eql("OK");
    expect(res[2][1]).to.eql(Buffer.from("QUEUED"));
    expect(res[3][1]).to.eql("QUEUED");
    expect(res[4][1]).to.eql([Buffer.from("hash"), data]);
    expect(res[5][1]).to.eql(data);
    done();
  });
});

