it('cy.scrollTo() - scroll the window or element to a position', () => {
  // https://on.cypress.io/scrollTo
  // You can scroll to 9 specific positions of an element:
  //  -----------------------------------
  // | topLeft        top       topRight |
  // |                                   |
  // |                                   |
  // |                                   |
  // | left          center        right |
  // |                                   |
  // |                                   |
  // |                                   |
  // | bottomLeft   bottom   bottomRight |
  //  -----------------------------------
