  cy.clearLocalStorage(/prop1|2/).should(ls => {
    expect(ls.getItem('prop1')).to.be.null;
    expect(ls.getItem('prop2')).to.be.null;
    expect(ls.getItem('prop3')).to.eq('magenta');
