it('v2 should render a state diagram when useMaxWidth is true (default)', () => {
  renderGraph(`
  stateDiagram-v2

  [*] --> State1
  State1 --> [*]
    `, {
    state: {
      useMaxWidth: true
    }
  });
  cy.get('svg').should(svg => {
    expect(svg).to.have.attr('width', '100%');
    expect(svg).to.have.attr('height');
    const height = parseFloat(svg.attr('height'));
    expect(height).to.be.within(177, 178);
    const style = svg.attr('style');
    expect(style).to.match(/^max-width: [\d.]+px;$/);
    const maxWidthValue = parseFloat(style.match(/[\d.]+/g).join('')); // use within because the absolute value can be slightly different depending on the environment Â±5%

    expect(maxWidthValue).to.be.within(135 * 0.95, 135 * 1.05);
  });
});

it('v2 should render a state diagram when useMaxWidth is false', () => {
  renderGraph(`
  stateDiagram-v2

  [*] --> State1
  State1 --> [*]
