it("should call simple before & after hooks", () => {
  service.schema.hooks = {
    before: {
      "*"(ctx) {
        FLOW.push("before-all-hook");
        ctx.params.hundred = 100;
      },

      find(ctx) {
        FLOW.push("before-hook");
        ctx.params.second = 2;
      }

    },
    after: {
      find(ctx, res) {
        FLOW.push("after-hook");
        return Object.assign(res, {
          b: 200
        });
      },

      "*"(ctx, res) {
        FLOW.push("after-all-hook");
        return Object.assign(res, {
          x: 999
        });
      }

    }
  };
  FLOW = [];
  const handler = jest.fn(ctx => {
    FLOW.push("handler-" + ctx.params.first + "-" + ctx.params.second);
    return broker.Promise.resolve({
      a: 100
    });
  });
  const newHandler = mw.localAction.call(broker, handler, action);
  const ctx = Context.create(broker, endpoint, {
    first: 1
  });
  return newHandler.call(broker, ctx).catch(protectReject).then(res => {
    expect(res).toEqual({
      a: 100,
      b: 200,
      c: 300,
      x: 999
    });
    expect(FLOW).toEqual(["before-all-hook", "before-hook", "before-action-hook", "handler-1-2", "after-action-hook", "after-hook", "after-all-hook"]);
    expect(handler).toHaveBeenCalledTimes(1);
  });
});

