        err.b = 200;
        throw err;
      }],
      "*": [function (ctx, err) {
        FLOW.push("error-all-hook-1");
        err.x = 999;
        err.y = 888;
        throw err;
      }, function (ctx, err) {
        FLOW.push("error-all-hook-2");
        err.x = 909;
        throw err;
      }]
    }
  };
  FLOW = [];
  const error = new MoleculerError("Simple error");
  const handler = jest.fn(() => {
    FLOW.push("handler");
    return broker.Promise.reject(error);
  });
  const newHandler = mw.localAction.call(broker, handler, action);
  const ctx = Context.create(broker, endpoint, {
    first: 1
  });
  return newHandler.call(broker, ctx).then(protectReject).catch(err => {
    expect(err).toBe(error);
    expect(err.a).toBe(100);
    expect(err.b).toBe(200);
    expect(err.x).toBe(909);
    expect(err.y).toBe(888);
    expect(FLOW).toEqual(["before-action-hook", "handler", "error-action-hook", "error-hook-1", "error-hook-2", "error-all-hook-1", "error-all-hook-2"]);
    expect(handler).toHaveBeenCalledTimes(1);
  });
});

