it("should serialize the event packet", () => {
  const obj = {
    ver: "2",
    sender: "test-1",
    id: "event-id",
    event: "user.created",
    data: {
      a: 5,
      b: "Test"
    },
    broadcast: true,
    meta: {
      name: "John"
    },
    level: 5,
    tracing: true,
    parentID: "parent-id",
    requestID: "request-id",
    caller: "posts.created",
    needAck: true
