    expect(broker.call).toHaveBeenCalledWith("posts.find", {
      limit: 2,
      offset: 0
    }, {
      timeout: 500
    });
    expect(broker.call).toHaveBeenCalledWith("users.find", {
      limit: 2,
      sort: "username"
    }, {
      timeout: 200
    });
  });
});

